#!/bin/bashgit initecho "Once upon a time in a faraway land, there was a magical forest." > story.txtgit add story.txtgit commit -m "Initial commit with the beginning of the story"git checkout -b add-characterecho "A brave knight named Arthur lived in the forest." >> story.txtgit add story.txtgit commit -m "Add a brave knight named Arthur"git checkout maingit merge add-charactergit branch -d add-charactergit checkout -b add-eventecho "One day, Arthur found a hidden treasure map." >> story.txtgit add story.txtgit commit -m "Add an event where Arthur finds a treasure map"git checkout maingit merge add-eventgit branch -d add-eventgit checkout -b finalize-storyecho "Arthur followed the map and found the treasure, becoming the richest knight in the land." >> story.txtgit add story.txtgit commit -m "Finalize the story"git checkout maingit merge finalize-storygit branch -d finalize-storygit tag -a v1.0 -m "Release version 1.0"git log --pretty=oneline --all > task2.txtecho "" >> task2.txtgit diff HEAD~1 HEAD >> task2.txtecho "" >> task2.txtgit log --graph >> task2.txt
history > task2.sh